import { useState } from "react";

const initialFriends = [
  {
    id: 118836,
    name: "Clark",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -7,
  },
  {
    id: 933372,
    name: "Sarah",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Anthony",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

export default function App() {
  const [friends, setFriends] = useState(initialFriends);
  const [isFormAddFriendOpen, setIsAddFriendOpen] = useState(false);
  return <div className="app">
    <div className="sidebar">
      <FriendsList friends={friends} />
      <FormAddFriend onSubmit={setFriends} />
      {/* <Button onClick={setIsAddFriendOpen(state => !state)}>{isFormAddFriendOpen ? 'Close' : 'Add friend'}</Button> */}
    </div>
  </div>
}

function FriendsList({ friends }) {
  return <ul className="">
    {friends.map(friend =>
      <li key={friend.id}>
        <img src={friend.image} alt={friend.name} />
        <h3>{friend.name}</h3>
        {friend.balance < 0 && <p className="red">You own {friend.name} ${Math.abs(friend.balance)}</p>}
        {friend.balance > 0 && <p className="green">{friend.name} ows you ${friend.balance}</p>}
        {friend.balance === 0 && <p>You and {friend.name} are even</p>}
        <Button>Select</Button>
      </li>)}
  </ul>
}

function FormAddFriend({ onSubmit }) {
  return <form className="form-add-friend">
    <label htmlFor="friend-name">Friend name</label>
    <input type="text" title="friend-name" name="friend-name" />

    <label htmlFor="friend-img">Image Url</label>
    <input type="text" title="friend-img" name="friend-img" />

    <Button>Add</Button>

  </form>
}

function Button({ children }) {
  return <button className="button">{children}</button>
}
